mapper.select:
SELECT * FROM board WHERE member_id = #{id}
AND subject LIKE CONCAT('%', #{cri.keyword}, '%')
AND content LIKE CONCAT('%', #{cri.keyword}, '%')
AND member_id LIKE CONCAT('%', #{cri.keyword}, '%')
ORDER BY re_ref DESC, re_seq ASC LIMIT #{cri.startRow}, #{cri.amount}

SELECT COUNT(*) AS cnt FROM comment WHERE member_id = #{id}
content LIKE CONCAT('%', #{cri.keyword}, '%')
member_id LIKE CONCAT('%', #{cri.keyword}, '%')

SELECT * FROM comment WHERE member_id = #{id}
AND content LIKE CONCAT('%', #{cri.keyword}, '%')
AND member_id LIKE CONCAT('%', #{cri.keyword}, '%')
ORDER BY reg_date LIMIT #{cri.startRow}, #{cri.amount}
